#define MAX_CHAR 256

typedef struct {
    char character;
    int frequency;
} CharFreq;

int compare(const void *a, const void *b) {
    return ((CharFreq*)b)->frequency - ((CharFreq*)a)->frequency;
}

void frequencySort(char *s, char *result) {
    int freq[MAX_CHAR] = {0};
    int length = strlen(s);
    for (int i = 0; i < length; i++) {
        freq[(int)s[i]]++;
    }
    CharFreq charFreq[MAX_CHAR];
    int k = 0;
    for (int i = 0; i < MAX_CHAR; i++) {
        if (freq[i] > 0) {
            charFreq[k].character = i;
            charFreq[k].frequency = freq[i];
            k++;
        }
    }
    qsort(charFreq, k, sizeof(CharFreq), compare);
    int index = 0;
    for (int i = 0; i < k; i++) {
        for (int j = 0; j < charFreq[i].frequency; j++) {
            result[index++] = charFreq[i].character;
        }
    }
    result[index] = '\0';
}

